import React , {Component} from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'

const Exercise = ((props) => {
  // <tr>
  //   <td>{props.exercise.username}</td>
  //   <td>{props.exercise.description}</td>
  //   <td>{props.exercise.duration}</td>
  //   <td>{props.exercise.date.substring(0, 10)}</td>
  //   <td>
  //     <Link to={"/edit/"+props.exercise._id}>Modifier</Link> | <a href="#" onClick={() => {props.deleteExercise(props.exercise._id)}}>Supprimer</a>
  //   </td>
  // </tr>
})

export default class ExercisesList extends Component {
  constructor(props) {
      super(props)

      this.deleteExercise = this.deleteExercise.bind(this)

      this.state = {exercises: []}

  }

  async componentDidMount() {
    axios.get('http://localhost:5000/exercises/')
          .then(res => {
            this.setState({exercises: res.data})
            console.log(this.state.exercises)
          })
          .catch(err => {console.log(err)})
  }

  deleteExercise(id) {
    axios.delete(`http://localhost:5000/exercises/${id}`)
         .then(res => {
            console.log(res.data)
          })
         .catch(err => {console.log(err)})

    this.setState({
      exercises: this.state.exercises.filter(el => el._id !== id)
    })
  }

  exerciseList(){
   this.state.exercises.map(currentExercise => {
    // console.log(currentExercise)
      return <Exercise exercise={currentExercise} deleteExercise={this.deleteExercise} key={currentExercise._id} />
    })
  }
render() {
  return (
    <div>
      <h1>Exercices enregistrés</h1>
      <table className="table">
        <thead className="thead-light">
          <tr>
            <th>Utilisateur</th>
            <th>Description</th>
            <th>Durée en minutes</th>
            <th>Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {this.exerciseList()}
        </tbody>
      </table>
    </div>
  )
}
}
